import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import "./home.css";

const socket = io("http://localhost:4005");

function Home() {
  const [systemOn, setSystemOn] = useState(true);
  const [mode, setMode] = useState("image");
  const [minute, setMinute] = useState("");
  const [second, setSecond] = useState("");
  const [price, setPrice] = useState("");
  const [settings, setSettings] = useState([]);

  useEffect(() => {
    socket.on("configUpdate", (config) => {
      setSystemOn(config.systemOn);
      setSettings(config.settings || []);
    });
    socket.emit("getConfig");
    return () => socket.off("configUpdate");
  }, []);

  const handleToggleSystem = () => {
    setSystemOn((prev) => {
      const newStatus = !prev;
      socket.emit("adminUpdateConfig", { systemOn: newStatus });
      return newStatus;
    });
  };

  const handleSave = () => {
    if (!minute && !second) {
      alert("กรุณากรอกเวลาอย่างน้อย 1 ช่อง");
      return;
    }
    if (!price) {
      alert("กรุณากรอกราคา");
      return;
    }
    const packageData = {
      id: Date.now(),
      mode,
      date: new Date().toLocaleString(),
      duration: `${minute ? minute + " นาที" : ""}${second ? (minute ? " " : "") + second + " วินาที" : ""}`,
      price: price,
    };
    socket.emit("addSetting", packageData);
    setMinute("");
    setSecond("");
    setPrice("");
    alert("บันทึกแพ็คเกจสำเร็จ");
  };

  return (
    <div className="admin-home-container">
      <header className="admin-header">
        <div className="admin-title">
          <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="#1a237e" strokeWidth="2">
            <rect x="2" y="3" width="20" height="14" rx="2" ry="2"/>
            <line x1="8" y1="21" x2="16" y2="21"/>
            <line x1="12" y1="17" x2="12" y2="21"/>
          </svg>
          <span>CMS ADMIN</span>
        </div>
        <div className="admin-menu">
          <a href="/TimeHistory">ประวัติการตั้งเวลา</a>
          <a href="/image-queue">ตรวจสอบรูปภาพ</a>
          <a href="/stat-slip">ตรวจสอบสลิป</a>
          <a href="/report">รายงาน</a>
        </div>
        <div className="admin-status">
          <span>สถานะระบบ:</span>
          <div
            className={`switch-track ${systemOn ? "switch-on" : "switch-off"}`}
            onClick={handleToggleSystem}
            style={{ cursor: "pointer" }}
            title={systemOn ? "ปิดระบบ" : "เปิดระบบ"}
          >
            <div className="switch-thumb"></div>
          </div>
        </div>
      </header>

      <main className="admin-main">
        <section className="mode-row">
          <span>ตั้งค่าสำหรับ:</span>
          <button
            className={`mode-btn${mode === "image" ? " active" : ""}`}
            onClick={() => setMode("image")}
            disabled={!systemOn}
          >
            รูปภาพ
          </button>
          <button
            className={`mode-btn${mode === "text" ? " active" : ""}`}
            onClick={() => setMode("text")}
            disabled={!systemOn}
          >
            ข้อความ
          </button>
        </section>
        {!systemOn && (
          <div className="system-off-msg">
            * ระบบถูกปิด ฝั่ง user จะไม่สามารถเลือกส่งภาพหรือข้อความได้
          </div>
        )}

        <section className="setting-row">
          <div className="time-row">
            <label>ตั้งเวลา (เลือกอย่างน้อย 1 ช่อง):</label>
            <div className="time-inputs">
              <input
                type="number"
                min="1"
                max="59"
                placeholder="นาที"
                value={minute}
                onChange={(e) => setMinute(e.target.value)}
                disabled={!systemOn}
              />
              <input
                type="number"
                min="1"
                max="59"
                placeholder="วินาที"
                value={second}
                onChange={(e) => setSecond(e.target.value)}
                disabled={!systemOn}
              />
            </div>
          </div>
          <div className="price-row">
            <label>ตั้งราคา (บาท):</label>
            <input
              type="number"
              min="1"
              placeholder="ราคา"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              disabled={!systemOn}
            />
          </div>
          <button
            className="save-btn"
            onClick={handleSave}
            disabled={!systemOn}
          >
            บันทึกแพ็คเกจ
          </button>
        </section>

        {/*
        <section className="history-row">
          <h3>แพ็คเกจที่ตั้งไว้</h3>
          <div className="package-list">
            {settings.length === 0 ? (
              <div className="empty-history">ยังไม่มีแพ็คเกจที่ตั้งไว้</div>
            ) : (
              settings.map((pkg) => (
                <div key={pkg.id} className="package-item">
                  <div className="package-mode">{pkg.mode === "image" ? "รูปภาพ" : "ข้อความ"}</div>
                  <div className="package-duration">{pkg.duration}</div>
                  <div className="package-price">฿{pkg.price}</div>
                  <div className="package-date">{pkg.date}</div>
                </div>
              ))
            )}
          </div>
        </section>
        */}
      </main>
    </div>
  );
}

export default Home;
