import React, { useState, useRef } from "react";
import "./LuckyWheel.css";

function getRandomInt(min, max) {
  return Math.floor(Math.random() * (max - min + 1)) + min;
}

const defaultColors = [
  "#f87171", "#fbbf24", "#34d399", "#60a5fa", "#a78bfa", 
  "#f472b6", "#facc15", "#4ade80", "#38bdf8", "#818cf8"
];

function LuckyWheel() {
  const [segments, setSegments] = useState(["โต๊ะ 1", "โต๊ะ 2", "โต๊ะ 3"]);
  const [spinning, setSpinning] = useState(false);
  const [winner, setWinner] = useState(null);
  const wheelRef = useRef(null);

  const spinWheel = () => {
    if (segments.length < 2 || spinning) return;
    setWinner(null);
    setSpinning(true);

    const winnerIdx = getRandomInt(0, segments.length - 1);
    const degPerSeg = 360 / segments.length;
    const randomTurns = getRandomInt(5, 8);
    const finalDeg = 360 * randomTurns + (winnerIdx * degPerSeg + degPerSeg / 2);

    if (wheelRef.current) {
      wheelRef.current.style.transition = "none";
      wheelRef.current.style.transform = `rotate(0deg)`;
      setTimeout(() => {
        wheelRef.current.style.transition = "transform 5s cubic-bezier(.17,.67,.83,.67)";
        wheelRef.current.style.transform = `rotate(-${finalDeg}deg)`;
      }, 50);
    }

    setTimeout(() => {
      setSpinning(false);
      setWinner(winnerIdx);
    }, 5200);
  };

  const renderWheel = () => {
    const segs = segments.length;
    const arc = 2 * Math.PI / segs;
    const radius = 200;
    const viewBox = 450;

    return (
      <svg width={viewBox} height={viewBox} viewBox={`0 0 ${viewBox} ${viewBox}`}>
        <g transform={`translate(${viewBox / 2},${viewBox / 2})`}>
          {segments.map((seg, i) => {
            const startAngle = i * arc - Math.PI / 2;
            const endAngle = (i + 1) * arc - Math.PI / 2;
            const x1 = radius * Math.cos(startAngle);
            const y1 = radius * Math.sin(startAngle);
            const x2 = radius * Math.cos(endAngle);
            const y2 = radius * Math.sin(endAngle);
            const largeArc = arc > Math.PI ? 1 : 0;

            const pathData = `
              M 0 0
              L ${x1} ${y1}
              A ${radius} ${radius} 0 ${largeArc} 1 ${x2} ${y2}
              Z
            `;

            return (
              <g key={i}>
                <path
                  d={pathData}
                  fill={defaultColors[i % defaultColors.length]}
                  stroke="#fff"
                  strokeWidth="2"
                />
                <text
                  x={(radius - 30) * Math.cos(startAngle + arc / 2)}
                  y={(radius - 30) * Math.sin(startAngle + arc / 2)}
                  textAnchor="middle"
                  alignmentBaseline="middle"
                  fontSize={segs > 20 ? 10 : 14}
                  fill="#222"
                  transform={`rotate(${(startAngle + arc / 2) * 180 / Math.PI},${(radius - 30) * Math.cos(startAngle + arc / 2)},${(radius - 30) * Math.sin(startAngle + arc / 2)})`}
                  style={{ userSelect: "none", pointerEvents: "none" }}
                >
                  {seg}
                </text>
              </g>
            );
          })}
        </g>
      </svg>
    );
  };

  return (
    <div className="lucky-wheel-container">
      <h2>วงล้อเสี่ยงดวง</h2>
      <div className="wheel-area">
        {/* ลูกศร pointer */}
        <div className="wheel-pointer" />
        <div
          className="wheel-svg"
          ref={wheelRef}
          style={{
            width: 450,
            height: 450,
            margin: "0 auto",
            borderRadius: "50%",
            boxShadow: "0 2px 16px rgba(0,0,0,0.2)",
            background: "#fff",
            transition: "transform 5s cubic-bezier(.17,.67,.83,.67)"
          }}
        >
          {renderWheel()}
        </div>
      </div>
      <button
        className="spin-btn"
        onClick={spinWheel}
        disabled={spinning || segments.length < 2}
        style={{ marginTop: 24, fontSize: 22, padding: "10px 40px" }}
      >
        {spinning ? "กำลังหมุน..." : "หมุนวงล้อ"}
      </button>
      {winner !== null && (
        <div className="winner-result">
          🎉 ผลลัพธ์: <b>{segments[winner]}</b>
        </div>
      )}
    </div>
  );
}

export default LuckyWheel;
