import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { io } from "socket.io-client";
import "./home.css";

const socket = io("http://localhost:4005"); // ให้ตรงกับ backend

function Home() {
  const [systemOn, setSystemOn] = useState(true);
  const [mode, setMode] = useState("image");
  const [second, setSecond] = useState("");
  const [minute, setMinute] = useState("");
  const [hour, setHour] = useState("");
  const [price, setPrice] = useState("");

  useEffect(() => {
    socket.on("configUpdate", (config) => {
      if (typeof config.systemOn === "boolean") {
        setSystemOn(config.systemOn);
      }
    });
    // ขอค่าล่าสุดจาก backend ทันทีที่เข้า
    socket.emit("getConfig");
    return () => socket.off("configUpdate");
  }, []);

  const handleSave = () => {
    const packageData = {
      id: Date.now(),
      mode,
      date: new Date().toLocaleString(),
      duration: `${hour || 0} ชม. ${minute || 0} นาที ${second || 0} วินาที`,
      price: price || 1,
    };
    // ส่งข้อมูลแพ็คเกจไป backend
    socket.emit("addSetting", packageData);

    alert(
      `บันทึกสำเร็จ\nประเภท: ${mode === "image" ? "รูปภาพ" : "ข้อความ"}\nเวลา: ${hour} ชม. ${minute} นาที ${second} วินาที\nราคา: ${price} บาท`
    );
  };

  const handleToggleSystem = () => {
    setSystemOn((prev) => {
      const newStatus = !prev;
      // ส่งสถานะใหม่ไป backend
      socket.emit("adminUpdateConfig", { systemOn: newStatus });
      return newStatus;
    });
  };

  return (
    <div className="home-container">
      {/* Header bar */}
      <header
        className="main-header"
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          maxWidth: "100vw",
          zIndex: 100,
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          padding: "1.2rem 2.5rem",
          boxSizing: "border-box",
          background: "#fff",
          boxShadow: "0 2px 12px rgba(30, 41, 59, 0.08)",
        }}
      >
        <div className="main-header-title" style={{ fontSize: "2.2rem", color: "#1a237e", fontWeight: "bold" }}>
          CMS ADMIN
        </div>
        <div className="menu-center">
          <Link to="/TimeHistory" className="menu-link">ประวัติการตั้งเวลา</Link>
          <Link to="/image-queue" className="menu-link menu-link-large">ตรวจสอบรูปภาพ</Link>
          <Link to="/stat-slip" className="menu-link">ตรวจสอบสลิป</Link>
          <Link to="/report" className="menu-link">รายงาน</Link>
        </div>
        <div className="status-row-header" style={{ display: "flex", alignItems: "center" }}>
          <span className="status-label" style={{ marginRight: 10 }}>สถานะระบบ:</span>
          <div
            className={`switch-track ${systemOn ? "switch-on" : "switch-off"}`}
            onClick={handleToggleSystem}
            style={{ cursor: "pointer" }}
            title={systemOn ? "ปิดระบบ" : "เปิดระบบ"}
          >
            <div className="switch-thumb"></div>
          </div>
        </div>
      </header>

      {/* Main content */}
      <main className="main-content" style={{ marginTop: "90px" }}>
        <section className="mode-row">
          <span>ตั้งค่าสำหรับ:</span>
          <button
            className={`mode-btn${mode === "image" ? " active" : ""}`}
            onClick={() => setMode("image")}
            disabled={!systemOn}
          >
            รูปภาพ
          </button>
          <button
            className={`mode-btn${mode === "text" ? " active" : ""}`}
            onClick={() => setMode("text")}
            disabled={!systemOn}
          >
            ข้อความ
          </button>
        </section>
        {!systemOn && (
          <div className="system-off-msg" style={{ marginTop: 10 }}>
            * ระบบถูกปิด ฝั่ง user จะไม่สามารถเลือกส่งภาพหรือข้อความได้
          </div>
        )}

        <section className="setting-row">
          <div className="time-row">
            <label>ตั้งเวลา (เลือกอย่างน้อย 1 ช่อง):</label>
            <div className="time-inputs">
              <input
                type="number"
                min="1"
                max="59"
                placeholder="วินาที"
                value={second}
                onChange={(e) => setSecond(e.target.value)}
                disabled={!systemOn}
              />
              <input
                type="number"
                min="1"
                max="59"
                placeholder="นาที"
                value={minute}
                onChange={(e) => setMinute(e.target.value)}
                disabled={!systemOn}
              />
              <input
                type="number"
                min="1"
                max="24"
                placeholder="ชั่วโมง"
                value={hour}
                onChange={(e) => setHour(e.target.value)}
                disabled={!systemOn}
              />
            </div>
          </div>

          <div className="price-row">
            <label>ตั้งราคา (บาท):</label>
            <input
              type="number"
              min="1"
              placeholder="ราคา"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              disabled={!systemOn}
            />
          </div>

          <button
            className="save-btn"
            onClick={handleSave}
            disabled={!systemOn}
          >
            บันทึก
          </button>
        </section>
      </main>
    </div>
  );
}

export default Home;
