import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./AdminReport.css";

function AdminReport() {
  const [report, setReport] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);
  const [statusList, setStatusList] = useState({});

  useEffect(() => {
    fetch("http://localhost:5001/api/admin/report")
      .then(res => res.json())
      .then(data => {
        setReport(data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  const MAX_LEN = 30;

  const handleStatus = (idx, status) => {
    setStatusList(prev => ({ ...prev, [idx]: status }));
  };

  const openDetailModal = (r) => {
    setSelectedReport(r);
    setShowDetailModal(true);
  };

  const closeDetailModal = () => {
    setShowDetailModal(false);
    setSelectedReport(null);
  };

  return (
    <div className="ar-wrapper">
      <div className="ar-header-bar">
        <div className="ar-actions-left">
          <Link to="/home">
            <button className="ar-btn secondary">Home</button>
          </Link>
          <Link to="/stat-slip">
            <button className="ar-btn primary">Stat-slip</button>
          </Link>
        </div>
        <h2 className="ar-title">รายการแจ้งปัญหาจากผู้ใช้</h2>
      </div>

      <div className="ar-card">
        {loading ? (
          <div className="ar-loading">กำลังโหลด...</div>
        ) : report.length === 0 ? (
          <div className="ar-empty">ยังไม่มีรายงาน</div>
        ) : (
          <div className="ar-table-wrap">
            <table className="ar-table">
              <thead>
                <tr>
                  <th>หมวดหมู่</th>
                  <th>รายละเอียด</th>
                  <th>เวลา</th>
                  <th>สถานะ</th>
                </tr>
              </thead>
              <tbody>
                {report.map((r, idx) => {
                  const isLong = r.detail.length > MAX_LEN;
                  const status = statusList[idx];
                  return (
                    <tr
                      key={idx}
                      className={`ar-row ${status ? `status-${status}` : ""}`}
                    >
                      <td>{r.category}</td>
                      <td className="ar-detail-cell">
                        {isLong ? (
                          <>
                            {r.detail.slice(0, MAX_LEN) + "..."}
                            <button
                              className="ar-link-button"
                              onClick={() => openDetailModal(r)}
                            >
                              ดูเพิ่ม
                            </button>
                          </>
                        ) : (
                          r.detail
                        )}
                      </td>
                      <td>{new Date(r.date).toLocaleString()}</td>
                      <td className="ar-status-col">
                        <div className="ar-status-buttons">
                          <button
                            className={`ar-status-btn ok ${status === "success" ? "active" : ""}`}
                            onClick={() => !status && handleStatus(idx, "success")}
                            disabled={!!status}
                            title="เรียบร้อย"
                          >
                            ✔
                          </button>
                          <button
                            className={`ar-status-btn problem ${status === "problem" ? "active" : ""}`}
                            onClick={() => !status && handleStatus(idx, "problem")}
                            disabled={!!status}
                            title="มีปัญหา"
                          >
                            ✖
                          </button>
                        </div>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {showDetailModal && selectedReport && (
        <div className="ar-modal-overlay" onClick={closeDetailModal}>
          <div
            className="ar-modal"
            onClick={e => e.stopPropagation()}
          >
            <div className="ar-modal-header">
              <h3>รายละเอียดการแจ้งปัญหา</h3>
              <button className="ar-modal-close" onClick={closeDetailModal}>✕</button>
            </div>

            <div className="ar-modal-body">
              <div className="ar-field">
                <span className="ar-label">ชื่อผู้แจ้ง:</span>
                <span>{selectedReport.name || "-"}</span>
              </div>
              <div className="ar-field">
                <span className="ar-label">เบอร์โทรศัพท์:</span>
                <span>{selectedReport.phone || "-"}</span>
              </div>
              <div className="ar-field column">
                <span className="ar-label">รายละเอียดปัญหา:</span>
                <div className="ar-detail-box">{selectedReport.detail}</div>
              </div>
              <div className="ar-field">
                <span className="ar-label">สถานะ:</span>
                <span>{selectedReport.status || "new"}</span>
              </div>
              <div className="ar-field">
                <span className="ar-label">วันที่แจ้ง:</span>
                <span>{selectedReport.date}</span>
              </div>
              {selectedReport.image && (
                <div className="ar-field column">
                  <span className="ar-label">รูปภาพ:</span>
                  <img
                    className="ar-report-image"
                    src={`http://localhost:5001/uploads/${selectedReport.image}`}
                    alt="แนบ"
                  />
                </div>
              )}
            </div>

            <div className="ar-modal-footer">
              <button className="ar-btn secondary" onClick={closeDetailModal}>
                ปิด
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AdminReport;
